-- Records of one_menu
-- ----------------------------

-- ----------------------------
-- Table structure for `one_news`
-- ----------------------------
DROP TABLE IF EXISTS `one_news`;
CREATE TABLE `one_news` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL,
  `uid` int(10) DEFAULT NULL,
  `label` varchar(255) DEFAULT NULL COMMENT '标签',
  `contents` text COMMENT '内容',
  `fid` int(10) DEFAULT NULL COMMENT '分类ID',
  `description` text COMMENT '描述',
  `img` varchar(255) DEFAULT NULL COMMENT '图片',
  `url` varchar(255) DEFAULT NULL COMMENT '外链',
  `state` int(10) DEFAULT NULL,
  `subject` int(10) DEFAULT '0' COMMENT '科目分类',
  `read_num` int(10) DEFAULT '0' COMMENT '阅读量',
  `good_num` int(10) DEFAULT NULL,
  `click_num` int(10) DEFAULT NULL COMMENT '点击量',
  `ctime` int(11) DEFAULT NULL,
  `utime` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=124 DEFAULT CHARSET=utf8;

