  `api_url` varchar(255) NOT NULL COMMENT '接入地址',
  `create_time` int(10) NOT NULL,
  `update_time` int(10) NOT NULL,
  `state` tinyint(1) NOT NULL COMMENT '状态',
  PRIMARY KEY (`id`)
