INSERT INTO `one_news` VALUES ('73', '神准！考驾照必看！你是哪种学员？', '3', '', '<p>\r\n	<img src=\"/Uploads/Editor/2016-12-16/5853631adebbc.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-16/58536372041b0.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-16/58536381f1840.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-16/58536389819c9.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-16/5853638fb15f5.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-16/585363977c311.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-16/585363b037db4.jpg\" alt=\"\" />\r\n</p>', '172', '', '154', '', '1', '0', '315', null, '6', '1481860146', null);
INSERT INTO `one_news` VALUES ('74', '学员必看：新政学费上不封顶！如何网上约考？', '3', '', '<p style=\"text-align:center;\">\r\n	<strong><img src=\"/Uploads/Editor/2016-12-19/585786c01f38b.jpg\" alt=\"\" /><br />\r\n</strong><strong><span style=\"font-size:18px;\">新政下，学费上不封顶！</span></strong>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:18px;\">学员该如何自主预约考驾照？</span></strong>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:18px;\">时尚教练为学员们一一解答！</span></strong>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">新政即计时培训收费，力求做到学车全透明、培训可核查、价格可追溯。</span></strong><span style=\"font-size:18px;\">计时培训收费即先学车培训后收费，学员需先首付</span><strong><span style=\"font-size:18px;\">1100-1500</span></strong><span style=\"font-size:18px;\">元，科目二</span><strong><span style=\"font-size:18px;\">16</span></strong><span style=\"font-size:18px;\">个学时、科目三</span><strong><span style=\"font-size:18px;\">24</span></strong><span style=\"font-size:18px;\">个学时，一起</span><strong><span style=\"font-size:18px;\">40</span></strong><span style=\"font-size:18px;\">个学时，学员在完成所有学时培训后，按照实际培训学时计时付费。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">计时收费标准约为：120-180元/小时，节假日高峰期可能根据实际情况溢价，价格上不封顶。</span></strong><span style=\"font-size:18px;\">另外，补考一次需增加至少</span><strong><span style=\"font-size:18px;\">10</span></strong><span style=\"font-size:18px;\">个学时的培训费。过不过万得看自己的学车时间是否处于高峰期以及补考次数了。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">学员自主预约考驾照的流程如下：</span></strong>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;\">请学员务必先登陆旧的考试自主预约系统，查询自己是否符合预约条件。千万不要直接去新系统直接预约，不然如果预约之后才通知不符合预约条件的话，还需要本人跑去车管所去取消！巨！麻！烦！</span>\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-19/5857895379656.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\"><span style=\"color:#E53333;\">第一步</span>，进入旧的考试预约系统：输入身份证号码、密码、验证码进行查询。</span></strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">旧的考试预约系统查询网址如下：</span></strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">http://app.stc.gov.cn:8090/kszzyy/specialLogin.jsp</span></strong>\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-19/5857897c8e6bd.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第二步，点击资格及预约情况查询</span></strong>\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-19/58578984bad4b.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第三步，查看结果。如果符合报考条件，请看第四步。</span></strong>\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-19/5857899f29ce6.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第四步，搜索新的</span></strong><strong><span style=\"font-size:18px;\">考试预约系统“广</span></strong><strong><span style=\"font-size:18px;\">东交通安全综合服务管理平台”，或网址：http://gd.122.gov.cn/</span></strong>\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-19/585789a7ad4a9.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第五步，新用户注册。</span></strong>\r\n</p>\r\n<p>\r\n	<strong><img src=\"/Uploads/Editor/2016-12-19/585789af576c0.jpg\" alt=\"\" /><br />\r\n</strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第六步，在”个人用户注册”页面，“填写用户信息”下，填写所有信息，完成后，将进入下一步操作“选择用户类型”。</span></strong>\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-19/585789b7dcf8d.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第七步，在“选择用户类型”下，选择“初次申领机动车驾驶证学员”选项。将进入“验证”。</span></strong>\r\n</p>\r\n<p>\r\n	<strong><img src=\"/Uploads/Editor/2016-12-19/585789c1c99c5.jpg\" alt=\"\" /><br />\r\n</strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第八步，在“验证”下，完成验证操作，将进入“注册成功”。</span></strong>\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-19/585789ca59861.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第九步，在“注册成功”下，点击“现在登陆按钮”。</span></strong>\r\n</p>\r\n<p>\r\n	<strong><img src=\"/Uploads/Editor/2016-12-19/585789d8e3164.jpg\" alt=\"\" /><br />\r\n</strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第十步，注册成功后可直接登陆。或在“广东交通安全服务管理平台”主页，在右上角点击“个人登陆”。</span></strong>\r\n</p>\r\n<p>\r\n	<strong><img src=\"/Uploads/Editor/2016-12-19/585789e098b00.jpg\" alt=\"\" /><br />\r\n</strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第十一步，在“个人登陆”下，进行登陆操作。将进入“我的主页”。</span></strong>\r\n</p>\r\n<p>\r\n	<strong><img src=\"/Uploads/Editor/2016-12-19/585789e84bcc7.jpg\" alt=\"\" /><br />\r\n</strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第十二步，登录成功后，在“我的主页”左侧，选择“驾驶证业务”，点击“考试预约”。</span></strong>\r\n</p>\r\n<p>\r\n	<strong><img src=\"/Uploads/Editor/2016-12-19/585789efb801b.jpg\" alt=\"\" /><br />\r\n</strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第十三步，在进入“驾驶证业务”条目下，在“业务须知”下，勾选“阅读并同意”，点击“下一步”。</span></strong>\r\n</p>\r\n<p>\r\n	<strong><img src=\"/Uploads/Editor/2016-12-19/585789f693468.jpg\" alt=\"\" /><br />\r\n</strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第十四步，进入“确认信息”后，检查所填信息是否正确，点击“下一步”。</span></strong>\r\n</p>\r\n<p>\r\n	<strong><img src=\"/Uploads/Editor/2016-12-19/585789fd8c3c4.jpg\" alt=\"\" /><br />\r\n</strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第十五步，在“选择可科目和考场”下，填写所有信息，点击“下一步”。</span></strong>\r\n</p>\r\n<p>\r\n	<strong><img src=\"/Uploads/Editor/2016-12-19/58578a040f8ce.jpg\" alt=\"\" /><br />\r\n</strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第十六步，在“现在考场场次”下，点击“下一步”。</span></strong>\r\n</p>\r\n<p>\r\n	<strong><img src=\"/Uploads/Editor/2016-12-19/58578a0c5a965.jpg\" alt=\"\" /><br />\r\n</strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第十七步，在“选择考试场次”下，选择。点击“下一步”。</span></strong>\r\n</p>\r\n<p>\r\n	<strong><img src=\"/Uploads/Editor/2016-12-19/58578a13d5845.jpg\" alt=\"\" /><br />\r\n</strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第十八步，在“确认提交”下，确认约考信息是否正确，然后完成手机验证，点击“完成”。</span></strong>\r\n</p>\r\n<p>\r\n	<strong><img src=\"/Uploads/Editor/2016-12-19/58578a1b6ef67.jpg\" alt=\"\" /><br />\r\n</strong>\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">↑↑</span><span style=\"font-size:18px;\">第十九步，“驾驶证业务”下的操作全部完成后，请记住自己的成功预约考试信息，并留意短信提醒，耐心等待预约结果。</span></strong>\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-19/58578a29d7ecc.jpg\" alt=\"\" />\r\n</p>', '101', '', '155', '', '1', '0', '288', null, '2', '1482132026', null);
INSERT INTO `one_news` VALUES ('75', '如何熟记交通规则？看这里！', '3', '', '<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>这些交通规则，你都知道吗？快，测下你能答对几题？</strong></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong><br />\r\n</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160707/7ca3ad52355d4796a8d40c2e9d6b00b1_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>这辆停在路边的机动车不违法？</strong></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">错。机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。警告标志与车的距离不足50米。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160707/b9871cb1031e4a0baf286160533a01af_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>机动车仪表板上（如图所示）亮时，提醒发动机需要补充机油？</strong></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span><span style=\"font-size:18px;\">错。这个是燃油液面高度指示灯。图中所示，说明是</span><span style=\"font-size:18px;\">燃油</span><span style=\"font-size:18px;\">已到最低液面。</span></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span><span style=\"font-size:18px;\"><br />\r\n</span></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160707/96a77f35ec334ab18ef4cdeeaaffd583_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\">路口最前端的双白虚线是什么含义？</span></strong> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\">停车让行线还是减速让行线？</span></strong> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">虚线可以压线，就是可以过去，边上有倒三角“让”字，所以是减速让行路口，单根白色实线是停止线，俩根白色实线是停车让行线，俩根白色虚线是减速让行线。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160707/fb0ef6ccbb7e4f20b3dfcb6d680ad9fa_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>如图所示，在这种情况下驶近路口，车辆只能直行还是可以左转或直行？</strong></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">路面上标记的是直行或左转，所以只能直行或者左转。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160707/c28698450fa84b2eba6fcd9b8f0487e7_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>这个标志是高速公路终点地名预告、行驶方向预告、还是距离预告？</strong></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">绿底的是高速公路标志，有地名，还有距离，此标志是高速公路地点距离预告。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160707/ee885f65807e467d955bad062f4ae017_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>这个标志代表非机动车车道还是自行车专用车道？</strong></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">此图为非机动车车道，别误以为自行车专用车道，没有自行车专用车道这一说。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160707/8205892f15bc47358f09d4075f66814c_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\">按下这个开关，后风窗玻璃除霜器就开始工作？</span></strong> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">错。这题表述的意思是：当前按下的这个图标即亮灯的这个表示的是前窗玻璃除霜器开始工作。 核心的意思是这两个图标，一个表示前窗玻璃除霜器，一个表示后窗玻璃除霜器。 带扇形的那个是前窗标识，下面那个长方形的是后窗标识。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160707/a3ac8a42767f4ff384e49fee187a604e_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>图中标注车辆在该地点停车是可以的？</strong></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">错。公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30以内的路段，除使用上述设施外，不得停车。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160707/b646fbba9233464a8601f843918a0c7d_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\"><strong>机动车仪表板上（如图所示）亮，提示发电机向蓄电池充电？</strong></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">错。提示发电机不向蓄电池充电了，充电系统有故障了！</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160707/8ac4fc4571d74d898777d0a125acb691_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>图中圈内两条白色虚线是什么标线？</strong></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">小型车转弯线、掉头引导线、还是左转弯待转区线？</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">直行灯变绿后，要左转的可以行驶进白色虚线内，等灯变成左转时就可以通行了。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>希望学员们能记住这些常见交通规则，开心快乐出行。</strong></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong><img src=\"/Uploads/Editor/2016-12-19/5857a29202920.jpg\" alt=\"\" /><br />\r\n</strong></span>\r\n</p>', '104', '', '156', '', '1', '0', '155', null, '3', '1482137986', null);
INSERT INTO `one_news` VALUES ('76', '恭喜新世纪驾校与时尚教练达成战略合作！', '3', '', '<p>\r\n	<img src=\"/Uploads/Editor/2016-12-21/5859dfef06078.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-21/5859dff614c5d.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-21/5859dffd35ea8.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-21/5859e004929cb.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-21/5859e00b69c9a.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-21/5859e01284702.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-21/5859e018cb08b.jpg\" alt=\"\" />\r\n</p>', '172', '', '157', '', '1', '0', '42', null, '1', '1482285087', null);
INSERT INTO `one_news` VALUES ('77', '2大神技巧 | 新手倒车入库', '3', '', '<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>倒车入库一直是大多新手司机的痛点，时尚教练总结了2点神技巧，希望能帮到新手学员们。</strong></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>1. 消除盲区：看后视镜！</strong></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">倒车时为了避开车身侧面的低矮障碍物、凹陷的地面，可以依靠观看后视镜来实现。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160702/969259256846487396277e2fdd78cba7_th.jpg\" alt=\"\" /><span style=\"font-size:18px;\"></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>左侧后视镜</strong></span><span style=\"font-size:18px;\"><strong>。</strong>借助左侧后视镜能方便判断车身与后方障碍物的距离、判断车身是否已停正。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160702/5eee57480d724b1ab2bb7d7632cbbda5_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>右侧后视镜</strong></span><span style=\"font-size:18px;\"><strong>。</strong>借助右侧后视镜能观察车身右侧有没有障碍物、轮胎离路沿的距离。了解右侧的路面情况，有时还可在倒车时将这面镜子的角度适当调低。部分轿车此侧后视镜是与变速箱联动的，只要挂入倒档，就会自动调低10至15度。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\">2. 倒车时忘记入库这件小事</span></strong> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>不少人反映：</strong>日常倒车与考试中的倒车入库存在差异，考试中的直角倒车入库不能生搬硬套到日常倒车中。以下是平时倒车的几点Tips：</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>Step 1</strong></span><span style=\"font-size:18px;\"><strong>：</strong>在“非”字形停车位前，当车辆接近要停的车位时，减速，先把方向向着自己要停的车位稍稍打一把，让车头微微探入要停的车位。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>Step 2</strong></span><span style=\"font-size:18px;\"><strong>：</strong>此时可以迅速地把这个车位扫视一下，同时迅速地向反方向打方向，让车头向着背离车位的方向运动。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>Step 3</strong></span><span style=\"font-size:18px;\"><strong>：</strong>充分利用道路的宽度，尽量使车与道路呈比较大的夹角，一直到车头接近可用路面的边缘时，再次迅速地回方向。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>Step 4</strong></span><span style=\"font-size:18px;\"><strong>：</strong>前三步顺利完成的话，你的车辆与道路之间的夹角至少已经有了45度。此时，瞄一眼后视镜，怎么样？至少从一面后视镜里已经能看见大半个车位了吧？</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>Step 5</strong></span><span style=\"font-size:18px;\"><strong>：</strong>看后视镜，慢慢地把车倒入车位。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><img src=\"http://img.mp.itc.cn/upload/20160702/dec2b8a96895446fb192570a7db7c2f9_th.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><img src=\"/Uploads/Editor/2016-12-21/5859e12c92fbd.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>', '104', '', '160', '', '1', '0', '116', null, '0', '1482285800', null);
INSERT INTO `one_news` VALUES ('78', '如何火速记住自动挡汽车档位？', '3', '', '<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>考驾照学车是手动挡汽车，买的车却是自动挡，你是不是其中一个？</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">宝宝心里苦，表示自动车挡位看不懂啊！</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160627/5625687a0f7a4fa3ad5b1a44159e6ad8_th.jpg\" alt=\"\" /><span style=\"font-size:18px;\"></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>自动挡一般有以下几个挡位，火速记住吧~</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160627/6b4407e1dbdf43b2bbca6272d93caa50_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\"><strong>P挡—Parking—停车挡</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">挂P挡时，变速器就会用一个“钩子”来钩住变速器输出端的某零件，从而锁紧汽车的传动部分。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160627/823e03e3969c4e3f84404b53671a8e7a_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>N档—Neutral—空档</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">空挡一般是在短暂的停车中使用。如果将变速档杆放置在N挡上的话，汽车引擎就会和变速器之间的动力传递系统分开。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160627/17ef183522de43b28e122d731b04b80b_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>R档—Reverse—倒车档</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\">在倒车的时候才会用得上。一般需要按下变速档上面的保险按钮装置才可将变速杆摆放到R挡上面。在车辆未完全挺稳时，不得强行转至“R”挡，否则就会导致变速器损坏。在汽车前进的状态下不能够使用R档，必须要在停止的时候才可以使用。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\"><strong>D档—Drive—前进档位</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">汽车前进行驶中使用的档位。大部分的自动档汽车D档位上面设计了1挡到高挡排序，有的是2挡到高挡排序的，开车的时候只要把变速拨杆放在D挡上面汽车的速度就会由你的油门来控制。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160627/08989e60722e4b17a79c6c8205b1de49_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>S挡—Sport—运动模式</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">用于爬坡与快速超速，省油，还不伤车。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">但不建议长时间处于“S”挡，超车后应立即换回D档，否则就会增加油耗。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">S档是高档位，1~5档它进行自动换档位。市内道路驾车用低档位；长途高速用高档位。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160627/f397f3e6bf3149e9992a1eea8b7c14d1_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>M挡—手动模式</strong> 当挡位挂在M挡时，换挡就需要司机自己手动来完成了。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>手自一体车型的带换挡杆附近会出现 “+ \"“-” 这两个符号</strong>，“+”号代表升挡，“-”号代表降挡，也有的车型使用的是拨片换挡，位置在方向盘下方，左右各一个。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160627/6a541d0cda334517aa40471de8e74c65_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>数字挡</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">有些自动挡车型在D挡下面会有3、2、1三个数字，这三个数字也都是前进挡。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>3</strong>代表挡位限制在1挡至3挡之间不会升至更高的挡位，可以在超车时使用;</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>2</strong>代表挡位限制在1挡和2挡之间，用于湿滑路况的起步或慢速前进时限制挡位的作用;</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>1</strong>代表挡位限制在1挡，可以用于爬坡等需要大扭矩输出时使用。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160627/6f6c04bd696c4c19bda6851f973234e6_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>L挡—低速挡</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">和上面的数字挡“1”是一个意思，都是为了把挡位限制在最低挡，得到大扭矩而不升挡。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><img src=\"/Uploads/Editor/2016-12-21/5859e4131ae33.jpg\" alt=\"\" /><br />\r\n</span>\r\n</p>', '104', '', '159', '', '1', '0', '193', null, '1', '1482286157', null);
INSERT INTO `one_news` VALUES ('79', '8种交通警察手势 测测你懂几种？', '3', '', '<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160621/841469c70cc64b33996ebab163d4dd16_th.jpg\" alt=\"\" /><span style=\"font-size:18px;\"></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\">8种交通警察手势 快测测你懂几种？</span></strong>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160621/9bb63c8675804dccbe4f5486243e0d25.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>停止信号</strong></span><span style=\"font-size:18px;\">：不准前方车辆通行（</span><span style=\"font-size:18px;\">交警左臂向前上方直伸，掌心向前</span><span style=\"font-size:18px;\">）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">　</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160621/e858533e334b4cd9a59d0fef14ee5ee7.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>直行信号</strong></span><span style=\"font-size:18px;\">：准许右方直行的车辆通行（</span><span style=\"font-size:18px;\">交警左臂向右平伸，掌心向前；右臂向右平伸，掌心向前，向左摆动</span><span style=\"font-size:18px;\">）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160621/dd8b9f04597d42b0a5315eca50cc814e.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>左转弯信号</strong>：</span><span style=\"font-size:18px;\">准许车辆左转弯，在不妨碍被放车辆通行的情况下可以调头（</span><span style=\"font-size:18px;\">交警右臂向前平伸，掌心向前，此时交警正前方车辆停止通行；左臂与手掌平直向右前方摆动，掌心向右</span><span style=\"font-size:18px;\">）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160621/f67f82920e8740cb9e725947057a73f3.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>左转弯待转信号</strong></span><span style=\"font-size:18px;\">：准许左方左转弯的车辆进入路口，沿左转弯行驶方向靠近路口中心，等候左转弯信号（</span><span style=\"font-size:18px;\">交警左臂向下方平伸，掌心向下；左臂与手掌平直向下方摆动</span><span style=\"font-size:18px;\">）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160621/5218bfe8c8b040cc967563b252c54bb0.jpg\" alt=\"\" /><span style=\"font-size:18px;\"><strong></strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>右转弯信号</strong></span><span style=\"font-size:18px;\">：准许右方的车辆右转弯（</span><span style=\"font-size:18px;\">交警左臂向前平伸，掌心向前，此时面对交警正前方的车辆停止通行；右臂与手掌平直向左前方摆动，手掌向左</span><span style=\"font-size:18px;\">）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160621/f881c77d46db42ce942e77164824ab67.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>变道信号</strong>：</span><span style=\"font-size:18px;\">车辆腾空指定的车道，减速慢行（</span><span style=\"font-size:18px;\">交警右臂向前平伸，掌心向左；右臂向左水平摆动</span><span style=\"font-size:18px;\">。）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160621/28c01c2dae9b40f4accbced2b5dc1ea6.jpg\" alt=\"\" /><span style=\"font-size:18px;\"><strong></strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>减速慢行信号</strong></span><span style=\"font-size:18px;\">：车辆减速慢行（</span><span style=\"font-size:18px;\">交警右臂向右前方平伸，掌心向下；右臂与手掌平直向下方摆动</span><span style=\"font-size:18px;\">）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160621/c124a9aa50ac4f70a4948771948bcb52.jpg\" alt=\"\" /><span style=\"font-size:18px;\"><strong></strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>靠边停车信号</strong></span><span style=\"font-size:18px;\">：车辆靠边停车（</span><span style=\"font-size:18px;\">交警左臂向前上方平伸，掌心向前；右臂向前下方平伸，掌心向左；右臂向左水平摆动</span><span style=\"font-size:18px;\">）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160621/4b871896ff8542218f4bd7c13932ee52.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>怎么样，8大手势，你GET到没？</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong><img src=\"/Uploads/Editor/2016-12-21/5859ef3e68b4c.jpg\" alt=\"\" /><br />\r\n</strong></span>\r\n</p>', '104', '', '161', '', '1', '0', '805', null, '7', '1482291402', null);
INSERT INTO `one_news` VALUES ('80', '车内仪表盘指示灯 你能答对几个？', '3', '', '<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>测下这些车内标示图，你答对了吗？</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong><br />\r\n</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160616/c4991af5988a43ffa5cb954c022d5024_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>灯光开关在该位置时，后雾灯点亮。</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">（对，</span><span style=\"font-size:18px;\">前雾灯朝左，后雾灯朝右</span><span style=\"font-size:18px;\">）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160616/e0d90d38e6d541438d6f1a135dd23f91_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>点火开关在ON位置，车用电器不能使用。</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">（错！</span><span style=\"font-size:18px;\">LOCK--锁止；ACC--断电；ON--通电；START--启动</span><span style=\"font-size:18px;\">）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160616/8689f33a935b48b6a5d035677f27de00_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>机动车仪表板上一直亮，表示发动机控制系统故障。</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">（对！你看它长的多像发动机。</span><span style=\"font-size:18px;\">亮着就表示出故障了。</span><span style=\"font-size:18px;\">）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160616/d3f78a31b34c46c084c1ff2b854ab0da_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>开启前照灯近光时仪表板上亮起。</strong>（错！这个图标表示的是</span><span style=\"font-size:18px;\">远光灯亮起</span><span style=\"font-size:18px;\">）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160616/8a4987706c3449e39f35ab32f9a8ea0f_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>机动车仪表板上一直亮表示什么？</strong>（</span><span style=\"font-size:18px;\">安全气囊处于故障状态</span><span style=\"font-size:18px;\">）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160616/c54e860ed16c4bffa4bd8f98211effe5_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>仪表显示当前发动机转速是6000转/分钟。</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">（错！此仪表中有KM，这是</span><span style=\"font-size:18px;\">速度和里程表</span><span style=\"font-size:18px;\">，表示当前汽车的行驶速度是60公里/小时）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160616/bc22a96e25904c6e98c086c46bef925e_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>机动车仪表板上亮表示什么？</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">（</span><span style=\"font-size:18px;\">后雾灯打开</span><span style=\"font-size:18px;\">。斜线在半圆形那侧的是前雾灯打开；斜线在直线那边的是后雾灯打开）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160616/a47b15daabc5408db6a29c016a9f47a6_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>这个仪表是发动机转速表？</strong>（对！跟时速表对比记忆。）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160616/c59f399c66b84fd1a9d209e1a9041886_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>机动车仪表板上亮表示什么？</strong>（</span><span style=\"font-size:18px;\">驻车制动器处于制动状态</span><span style=\"font-size:18px;\">。）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160616/de0ab4268d974766a6a3acdd0268f91b_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>哪个指示灯亮时，表示防抱死制动系统出现故障？</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">（图3。图1表示机油指示灯；图2表示前雾灯显示；图3是ABS指示灯，</span><span style=\"font-size:18px;\">ABS就是防抱死制动系统的简称</span><span style=\"font-size:18px;\">；图4是水温指示灯。）</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><img src=\"/Uploads/Editor/2016-12-22/585b3f4d7cc69.jpg\" alt=\"\" /><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>', '104', '', '163', '', '1', '0', '353', null, '11', '1482375146', null);
INSERT INTO `one_news` VALUES ('81', '怕违规抄牌？ 火速记住这些交通信号（学员请收藏）', '3', '', '<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>怕违规抄牌？ 学员请火速记住这些交通信号！</strong></span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"/Uploads/Editor/2016-12-22/585b6d1cc8992.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>1. 驾驶机动车在这种信号灯亮的路口，可以右转弯吗？</strong>（可以）</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"/Uploads/Editor/2016-12-22/585b6d310393b.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>2. 驾驶机动车行驶到这个位置时，如果车前轮已越过停止线可以继续通过吗？</strong>（不可以）</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"/Uploads/Editor/2016-12-22/585b6d4c61fd1.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\">3. 驾驶机动车遇到这种信号灯，可在对面直行车前直接向左转弯吗？</span></strong><span style=\"line-height:1.5;font-size:18px;\">（不可以）</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"/Uploads/Editor/2016-12-22/585b6da186c4c.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>4. 驾驶机动车在前方路口不能右转弯吗？</strong>（可以）</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"/Uploads/Editor/2016-12-22/585b6db1105ca.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>5. 驾驶机动车遇到这种信号灯亮时，如果已越过停止线，可以继续通行吗？</strong>（可以）</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"/Uploads/Editor/2016-12-22/585b6dc03f65f.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>6. 驾驶机动车遇到这种信号灯不断闪烁时怎样行驶？</strong>注意瞭望安全通过！而不是靠边停车等待或尽快加速通过哦。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160613/faefb8124e5e4714bd2350ea24e67780_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>7. 环形平面交叉？平面交叉路口？注意交互式道路？</strong>原来是注意分离式道路。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160613/94173e1c1d13481e8352fb80016c7d57_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>8. 限时进入？减速行驶？禁止驶入？</strong>原来是禁止通行的意思。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160613/087c9d58111f4f6ba0cba92845f18337_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>9. 禁止停放车辆还是禁止临时停车？</strong>这个是禁止长时停车，要记住哦。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160613/dd8813e17f6540cfb31364b3a8cfdda9_th.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><strong>10. 会车时停车让右侧车先行？</strong>这表示会车时停车让对方车先行。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><img src=\"/Uploads/Editor/2016-12-22/585b4100063c2.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span> \r\n</p>', '104', '', '165', '', '1', '0', '96', null, '2', '1482377757', null);
INSERT INTO `one_news` VALUES ('82', '新手女司机秒变老司机？不服你来试！', '3', '', '<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\"><strong>只要快速掌握以下学车技巧，女司机也能秒变老司机。</strong></span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160607/4ec09316e2a34ca2a74050cbbe88ae89_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\">不要直接着车！</span></strong><span style=\"font-size:18px;\">否则可能会打不着火，还会损害车辆点火系统。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\">在START</span></strong><strong><span style=\"font-size:18px;\">、ON</span></strong><strong><span style=\"font-size:18px;\">、ACC</span></strong><strong><span style=\"font-size:18px;\">、LOCK</span></strong><strong><span style=\"font-size:18px;\">每个挡位停留1-2</span></strong><strong><span style=\"font-size:18px;\">秒，听见通电声音后再进入下一挡位。</span></strong>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\"><br />\r\n</span></strong>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160607/e75d4ea36aae4622a83108699f0020b1_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\">千万不要连续启动车辆</span></strong>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">每次启动间隔应</span><strong><span style=\"font-size:18px;\">大于3秒</span></strong><span style=\"font-size:18px;\">；每次启动时间应</span><strong><span style=\"font-size:18px;\">少于10秒</span></strong>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">连续</span><strong><span style=\"font-size:18px;\">3次</span></strong><span style=\"font-size:18px;\">启动失败后，先拔出钥匙，</span><strong><span style=\"font-size:18px;\">1分钟</span></strong><span style=\"font-size:18px;\">后再启动</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160607/2e243ec87d8849608cf26267fa72e823_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\">不要加挡超车</span></strong>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\"><br />\r\n</span></strong>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160607/202cb8a9de984a1d9e68669fc08cb6a9_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\">不要不放手刹，习惯问题哦</span></strong>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\">不然会严重影响刹车寿命，还可能引发爆胎。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\">正确起步方式：踩下离合、启动汽车、打转向灯、挂一挡、放手刹、抬离合、加油门。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160607/f530e26a75b7486a95ed51775bad33aa_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><strong><span style=\"font-size:18px;\">不要</span></strong><span style=\"font-size:18px;\">原地打方向盘</span></strong>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\">转动方向盘时，不要让车停下来</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\">不然会造成助力系统漏油，还会缩短助力系统使用寿命，增加故障率。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160607/4473c840bed741739c1918e141214099_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><strong><span style=\"font-size:18px;\">不要</span></strong><span style=\"font-size:18px;\">着急开暖气</span></strong>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\">一般水温升到70摄氏度左右，就可以开暖风了</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160607/fb2fc9af4ef14b3694b3441914d9f76c_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\">不要左脚踩在离合上</span></strong>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\">除了换挡时，左脚不要放在离合上</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<img src=\"http://img.mp.itc.cn/upload/20160607/cc7eb22ae6404a2d969fcac0824cffb2_th.jpg\" alt=\"\" />\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\">不要不看后视镜</span></strong>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;line-height:1.5;\">多留意后视镜；后视镜要对准后挡风玻璃，观察车正后方的情况。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\">希望新手司机们能快速掌握以上技巧，快乐开心出行。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#333333;font-family:微软雅黑, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:18px;\"><img src=\"/Uploads/Editor/2016-12-22/585b7397cecba.jpg\" alt=\"\" /><br />\r\n</span>\r\n</p>', '104', '', '166', '', '1', '0', '495', null, '11', '1482388508', null);
INSERT INTO `one_news` VALUES ('83', '交管局翻旧账！强制扣分！12月26日执行', '3', '', '<p style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;line-height:38.4px;font-size:18px;color:#333333;background-color:#FFFFFF;\"><strong>交管局翻旧账！强制扣分！12月26日执行</strong></span> \r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-23/585d02bdb5333.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#E53333;\"><strong>特紧急通知（都怪小编，才收到风）</strong></span><span style=\"color:#888888;\"><span style=\"color:#000000;font-size:18px;\"><strong></strong></span><span style=\"font-size:16px;\"><strong></strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#888888;\"><strong><span style=\"font-size:18px;color:#333333;\">广东省交管局年底就交通违章翻起了旧账！ 广东省多个城市车管所已发通知： 2017年1月1日起，全省将对交通违法执行全面扣分！广东省所有地区、所有车辆、所有交通违法行为，应记必记，扣分绝不手软！</span></strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#888888;\"><span style=\"font-size:18px;color:#333333;\">所有交通违法行为，无论通过什么渠道处理，都要实行全面记分，包括终端机、违法处理窗口、手机APP。</span></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#888888;\"><strong><span style=\"font-size:18px;color:#333333;\">无论是1月1日前产生的不执行扣分违章，还是1月1日后产生的扣分违章，如果没有在12月26日前处理，一律按照新政策严格执行扣分！</span></strong><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#888888;\"><strong><span style=\"font-size:18px;\"><br />\r\n</span></strong></span> \r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-23/585d02d3abf30.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#333333;\">2017年1月1日起，所有交通违法行为，例如：超速20%以下都将被强制自动扣分！</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;font-size:18px;\"><strong>如您在12月26日前产生了不执行扣分违章或扣分违章行为，强烈建议您在12月26日前到车管所进行处理。不然所有的不执行扣分违章在12月26日后会被自动累计扣分！不知不觉扣满12分，怕怕！</strong></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#888888;\"><span style=\"font-size:16px;color:#000000;\"><strong><strong><span style=\"font-size:18px;color:#E53333;\"><br />\r\n</span></strong></strong></span></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#888888;\"><span style=\"font-size:16px;color:#000000;\"><strong><strong></strong><strong><span style=\"font-size:18px;color:#E53333;\">全面扣分交规时代！已来临！违章提醒：</span></strong></strong></span><span style=\"font-size:16px;\"><strong></strong><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#888888;\"><strong><span style=\"font-size:18px;color:#333333;\">1、</span></strong><span style=\"font-size:16px;\"><strong><span style=\"font-size:18px;color:#333333;\">12月26日</span></strong><span style=\"font-size:18px;color:#333333;\">起，广东省各地车管所交警大队将不再受理交通违章的办理</span></span></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#888888;\"><strong><span style=\"font-size:18px;color:#333333;\">2、</span></strong><span style=\"font-size:16px;\"><strong><span style=\"font-size:18px;color:#333333;\">12月26日</span></strong><span style=\"font-size:18px;color:#333333;\">起，广东省各省网上车管所也将不再处理交通违章的办理</span></span></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#888888;\"><strong><span style=\"font-size:18px;color:#333333;\">3、</span></strong><span style=\"font-size:16px;\"><strong><span style=\"font-size:18px;color:#333333;\">12月26日</span></strong><span style=\"font-size:18px;color:#333333;\">起，外省驾驶证将暂不能在广东省处理交通违章</span></span></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#888888;\"><strong><span style=\"font-size:18px;color:#333333;\">4、</span></strong><span style=\"font-size:18px;color:#333333;\"><strong>广东省各地将出现大量违章业务办理堆积、排队或赶超春运</strong></span></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#888888;\"><strong><span style=\"font-size:18px;color:#333333;\">5、办理时间或超出预期，或将面临退单</span></strong><strong><span style=\"font-size:16px;\"></span></strong></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#888888;\"><strong><span style=\"font-size:18px;color:#333333;\">6、未减速礼让斑马线、边开车变接听电话、抽烟、不及安全带、开车不按规定使用灯光、驾驶摩托车不戴安全头盔、不按规定超车等违法行为，明年1月1日起，这些看似“安全”的行为都将面临全面扣分处罚。</span></strong><strong><span style=\"font-size:16px;\"></span></strong></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#888888;\"><strong><span style=\"font-size:18px;color:#333333;\"><br />\r\n</span></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#E53333;\"><strong><strong>有哪些处理交通违法的渠道？</strong></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#888888;\"><strong></strong><strong><span style=\"line-height:1.6;font-size:18px;color:#333333;\">1. 去交警大队窗口排队缴费</span></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#888888;\"><strong><span style=\"line-height:1.6;font-size:18px;color:#333333;\"><img src=\"/Uploads/Editor/2016-12-23/585d035666392.jpg\" alt=\"\" /><br />\r\n</span></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#888888;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\">2. 在交警大队、车管所自助缴款机缴费</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;color:#333333;\">a.&nbsp;</span></strong><span style=\"color:#333333;font-size:18px;line-height:1.6;\">中重型以上载客载货汽车、营运客车、出租车、校车、危险品运输车不可以办理</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#888888;\"><strong><span style=\"font-size:18px;color:#333333;\">b.&nbsp;</span></strong><span style=\"line-height:25.6px;font-size:18px;color:#333333;\">罚款超过500元或记分超过6分的交通违法，当事人需携带身份证、驾驶证、行驶证原件，在窗口办理</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#888888;\"><span style=\"line-height:25.6px;font-size:18px;color:#333333;\"><img src=\"/Uploads/Editor/2016-12-23/585d0363daa89.jpg\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#888888;\"><img src=\"/Uploads/Editor/2016-12-23/585d0370b0372.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#888888;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#888888;\"><strong><span style=\"font-size:18px;color:#333333;\">3. 在微信缴费</span></strong><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#888888;\"><strong><img src=\"/Uploads/Editor/2016-12-23/585d037bdaa40.jpg\" alt=\"\" /><br />\r\n</strong></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"font-size:18px;color:#333333;\"><strong><span style=\"line-height:1.6;font-size:18px;color:#333333;\">点开我的钱包后，点击“生活缴费”</span></strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<div style=\"text-align:left;\">\r\n	<img src=\"/Uploads/Editor/2016-12-23/585d038838fb9.jpg\" alt=\"\" /><br />\r\n</div>\r\n<span style=\"font-size:18px;color:#888888;\"> \r\n<div style=\"text-align:left;\">\r\n	<strong><span style=\"line-height:1.6;color:#333333;\">&nbsp;</span><span style=\"line-height:1.6;color:#333333;\"><span> 进入“生活缴费”页面，点击“交通违章”</span></span></strong> \r\n</div>\r\n</span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<div style=\"text-align:left;\">\r\n	<span style=\"color:#333333;font-size:16px;line-height:1.6;\"></span><img src=\"/Uploads/Editor/2016-12-23/585d039462c32.jpg\" alt=\"\" /><br />\r\n</div>\r\n<span style=\"font-size:18px;color:#888888;\"> \r\n<p style=\"text-align:left;\">\r\n	<strong><span style=\"line-height:1.6;color:#333333;\">按要求依次输入如图资料，点击查询，便可查询到您的违规记录，然后进行缴费。</span></strong> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<img src=\"/Uploads/Editor/2016-12-23/585d02493fd9e.jpg\" alt=\"\" /> \r\n</p>\r\n</span>', '101', '', '170', '', '1', '0', '75', null, '1', '1482490470', null);
INSERT INTO `one_news` VALUES ('85', '教练招生杀手锏！招生招到手软！', '3', '', '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;\"><strong>“时尚教练”APP新版本已全线上市</strong></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;\"><strong>除驾考圈疯传的直播功能外</strong></span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;\"><strong>另有两大杀招，让教练招生招到手软！！</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;\"></span><img src=\"/Uploads/Editor/2016-12-24/585dd77c7867a.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#E53333;font-size:18px;\"><strong>杀招1：招生优惠券&amp;试学优惠券</strong></span> \r\n</p>\r\n<br />\r\n<p>\r\n	<span style=\"font-size:18px;\">编辑优惠券至微信、qq等社交平台，利用实际优惠吸引学员参与，留下手机号码，让潜在机会跑不出你们的掌握。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:18px;\">杀招秘笈</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;\"><br />\r\n</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;color:#E53333;\">1、点击招生优惠券（试学优惠券使用方法相同） &nbsp;</span></strong> \r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-24/585dd7bfa16dc.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;color:#E53333;\">2、编辑优惠金额后，点击发送优惠券即可分享</span></strong> \r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-24/585dd814a74c6.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF2941;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#E53333;font-size:18px;\"><strong>杀招2：考试管理&amp;学员管理</strong></span> \r\n</p>\r\n<br />\r\n<p>\r\n	<span style=\"font-size:18px;\">一键掌握学员进度，学员水平一目了然，合理安排学习与约考时间。</span> \r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-24/585dd847a3579.jpg\" alt=\"\" /> \r\n</p>\r\n<strong> \r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:18px;\"><br />\r\n</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:18px;\">杀招秘笈</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:18px;\"><br />\r\n</span></strong> \r\n</p>\r\n</strong> \r\n<p>\r\n	<strong><span style=\"font-size:18px;color:#E53333;\">1、进入考试管理，点击添加按钮，可以添加学员的相关考试信息，可以查看学员考试结果，还可以设置考前提醒功能。</span></strong> \r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-24/585dd86ddb910.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-24/585dd89488caa.jpg\" alt=\"\" /> \r\n</p>\r\n<br />\r\n<p>\r\n	<strong><span style=\"font-size:18px;color:#E53333;\">2、点击进入学员管理，可以按科目、车型、性别等条件进行排列。还可以直接拨打电话联系学员，免去寻找通讯录的步骤。</span></strong> \r\n</p>\r\n<p>\r\n	<img src=\"/Uploads/Editor/2016-12-24/585dd90430cae.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;\"><strong><span style=\"font-size:18px;\">时尚教练App，让学员主动找上门</span></strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;\"><strong><span style=\"font-size:18px;\">试试便知↓</span><strong><span style=\"font-size:18px;\">↓</span></strong></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.6;\"><img src=\"/Uploads/Editor/2016-12-24/585ddbe7331e3.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>', '172', '', '173', '', '1', '0', '43', null, '0', '1482546157', null);
